class DeskiChatScriptLoad { }
{
    DeskiChatScriptLoad.loadScript = function(name, url) {
        DeskiChatBuilder.addElement(
            name,
            'script',
            function(el) {
                el.src = url;
            }
        )
    }
}

class DeskiChatBuilder { }
{
    DeskiChatBuilder.addChatElement = function() {
        DeskiChatBuilder.addElement(
            'deski-react-root',
            'div',
            function(el) {
                el.id = 'deskichat-root';
            }
        )
    }

    DeskiChatBuilder.addElement = function(name, tag, update) {
        if (document[name]) {
            return;
        }
        document[name] = 1;

        var el = document.createElement(tag);
        update(el);
        document.body.appendChild(el);
    }
}

class DeskiChat { }
{
    DeskiChat.showChat = function() {
        DeskiChatBuilder.addChatElement();
        DeskiChatScriptLoad.loadScript('deski-react', 'https://static.deski.ru/js/chat.widget.min.js');
    }
} 

DeskiChat.showChat();